// src/components/UnreadPostsModal.js
import React from 'react';

const UnreadPostsModal = ({ notifications, onClose, onPostClick }) => {
  return (
    <div className="unread-posts-modal">
      <div className="modal-content">
        <button className="close-btn" onClick={onClose}>Close</button>
        <h3>Unread Posts</h3>
        {notifications.length > 0 ? (
          notifications.map((notif) => (
            <div
              key={notif._id}
              className="notification-card"
              onClick={() => onPostClick(notif.postId)}
            >
              <p>Posted by: <a href="#" onClick={(e) => { e.preventDefault(); onPostClick(notif.postId); }}>{notif.authorUsername}</a></p>
            </div>
          ))
        ) : (
          <p>No unread posts</p>
        )}
      </div>
    </div>
  );
};

export default UnreadPostsModal;
