// src/components/UnreadPostsModal.js
import React from 'react';
import API from '../api';

const UnreadPostsModal = ({ notifications, onClose, onPostClick }) => {
  const handlePostClick = async (postId) => {
    try {
      const { data: post } = await API.get(`/posts/${postId}`);
      window.open(`/post/${postId}`, '_blank'); // Open post in a new tab
      onPostClick(postId); // Mark notification as read
    } catch (error) {
      console.error('Error fetching post:', error);
      alert('ERROR\n' + error.message);
    }
  };

  return (
    <div className="unread-posts-modal">
      <div className="modal-content">
        <button className="close-btn" onClick={onClose}>Close</button>
        <h3>Unread Posts</h3>
        {notifications.length > 0 ? (
          notifications.map((notif) => (
            <div
              key={notif._id}
              className="notification-card"
              onClick={() => handlePostClick(notif.postId)}
            >
              <p>Posted by: <a href="#" onClick={(e) => e.preventDefault()}>{notif.authorUsername || "Anonymous"}</a></p>
            </div>
          ))
        ) : (
          <p>No unread posts</p>
        )}
      </div>
    </div>
  );
};

export default UnreadPostsModal;
