// src/components/Register.js
import React, { useState } from 'react';
import API from '../api';

const Register = ({ closeForm }) => {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    confirmPassword: ''
  });
  const [error, setError] = useState('');

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
    setError(''); // Clear any previous error when user starts typing
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Check if password and confirmPassword match
    if (formData.password !== formData.confirmPassword) {
      setError("Password and Confirm Password do not match");
      return; // Exit early if passwords don't match
    }

    try {
      // Send registration request with minimal checks
      await API.post('/auth/register', {
        username: formData.username,
        email: formData.email,
        password: formData.password
      });
      alert('Registration successful');
      window.location.href = '/dashboard'; // Redirect to dashboard on success
    } catch (error) {
      if (error.response && error.response.data.error === 'Email already registered') {
        setError('This email is already registered. Please use a different email.');
      } else {
        console.error('Error in registration:', error);
        setError('Registration failed. Please try again.');
      }
    }
  };

  return (
    <div className="form-overlay">
      <div className="form-container">
        <h2>Register</h2>
        <form onSubmit={handleSubmit}>
          <input name="username" placeholder="Username" onChange={handleChange} />
          <input name="email" type="text" placeholder="Email" onChange={handleChange} />
          <input name="password" type="password" placeholder="Password" onChange={handleChange} />
          <input name="confirmPassword" type="password" placeholder="Confirm Password" onChange={handleChange} />
          
          {error && <p style={{ color: 'red' }}>{error}</p>} {/* Display error if email is duplicate */}
          
          <button type="submit">Sign Up</button>
          <button type="button" className="close-btn" onClick={closeForm}>Close</button>
        </form>
      </div>
    </div>
  );
};

export default Register;
