// src/pages/Dashboard.js
import React, { useEffect, useState } from 'react';
import API from '../api';
import PostForm from '../components/PostForm';
import io from 'socket.io-client';
import '../styles/Dashboard.css';

const socket = io('http://localhost:5000');

const Dashboard = () => {
  const [posts, setPosts] = useState([]);
  const [showPostForm, setShowPostForm] = useState(false);
  const [notifications, setNotifications] = useState([]);
  const [showNotificationDot, setShowNotificationDot] = useState(false);
  const [unreadNotifications, setUnreadNotifications] = useState([]);

  const username = localStorage.getItem('username');
  const token = localStorage.getItem('token');

  // Fetch posts when component mounts
  const fetchPosts = async () => {
    try {
      const { data } = await API.get('/posts', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setPosts(data);
    } catch (error) {
      console.error('Error fetching posts:', error);
    }
  };

  // Fetch unread notifications
  const fetchUnreadNotifications = async () => {
    try {
      const { data } = await API.get('/notifications/unread', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUnreadNotifications(data);
      setShowNotificationDot(data.length > 0);
    } catch (error) {
      console.error("Error fetching notifications:", error);
    }
  };

  useEffect(() => {
    fetchPosts();
    fetchUnreadNotifications();

    socket.on('newPostNotification', (notification) => {
      if (notification.authorUsername !== username) {
        setUnreadNotifications((prev) => [notification, ...prev]);
        setShowNotificationDot(true);
      }
    });

    return () => {
      socket.off('newPostNotification');
    };
  }, [username]);

  const handlePostCreated = () => {
    fetchPosts();
    setShowPostForm(false);
  };

  const togglePostForm = () => {
    setShowPostForm((prev) => !prev);
  };

  const handleNotificationClick = () => {
    setShowNotificationDot(false);
    setNotifications([]);
  };

  const markAsReadAndOpenPost = async (postId) => {
    // Mark the notification as read on the server
    await API.post(`/notifications/markAsRead/${postId}`, {}, {
      headers: { Authorization: `Bearer ${token}` },
    });
    setUnreadNotifications(unreadNotifications.filter((notif) => notif.postId !== postId));
    window.open(`/post/${postId}`, '_blank');
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('username');
    window.location.href = '/';
  };

  return (
    <div className="dashboard">
      <div className="dashboard-header">
        <div className="username-box">
          <span>{username}</span>
        </div>

        <button onClick={togglePostForm}>Create Post</button>

        <div className="notification-box" onClick={handleNotificationClick}>
          <button>Notification</button>
          {showNotificationDot && <span className="notification-count">{unreadNotifications.length}</span>}
        </div>
      </div>

      {showPostForm && <PostForm onPostCreated={handlePostCreated} />}

      <div className="posts-container">
        <h2>Recent Posts</h2>
        {posts.length > 0 ? (
          posts.map((post) => (
            <div key={post._id} className="post-item">
              <p className="author-name">Posted by: {post.author?.username || "Anonymous"}</p>
              <h3>{post.title}</h3>
              {post.language && <p>Language: {post.language}</p>}
              {post.codeFileUrl && (
                <p>
                  Code Snippet File: <a href={post.codeFileUrl} download>Download Code</a>
                </p>
              )}
              {post.uploadedFileUrl && (
                <p>
                  Uploaded File: <a href={post.uploadedFileUrl} download>Download File</a>
                </p>
              )}
            </div>
          ))
        ) : (
          <p>No posts available</p>
        )}
      </div>

      <div className="unread-notifications">
        <h3>Unread Posts</h3>
        {unreadNotifications.length > 0 ? (
          unreadNotifications.map((notif) => (
            <div key={notif.postId} className="notification-item">
              <p>
                Posted by: <a href="#" onClick={() => markAsReadAndOpenPost(notif.postId)}>
                  {notif.authorUsername}
                </a>
              </p>
            </div>
          ))
        ) : (
          <p>No unread posts</p>
        )}
      </div>

      <button className="logout-button" onClick={handleLogout}>Logout</button>
    </div>
  );
};

export default Dashboard;
