import React, { useState } from 'react';
import API from '../api';

const Register = ({ closeForm }) => {
  const [formData, setFormData] = useState({ username: '', email: '', password: '' });
  const [error, setError] = useState('');

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
    setError('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      console.log('Attempting to register user:', formData);
      
      const { data } = await API.post('/auth/register', formData);

      if (data.token) {
        // Store token and redirect to dashboard if registration was successful
        localStorage.setItem('token', data.token);
        localStorage.setItem('username', formData.username);
        alert('Registration successful');
        window.location.href = '/dashboard'; // Redirect to dashboard
      } else {
        alert('Registration succeeded but no token received. Please log in.');
      }
    } catch (error) {
      setError(error.response?.data?.error || 'Server error. Please try again later.');
      console.error('Registration failed:', error.response?.data || error.message);
    }
  };

  return (
    <div className="form-overlay">
      <div className="form-container">
        <h2>Sign Up</h2>
        <form onSubmit={handleSubmit}>
          <input
            name="username"
            placeholder="Username"
            onChange={handleChange}
            value={formData.username}
            required
          />
          <input
            name="email"
            type="email"
            placeholder="Email"
            onChange={handleChange}
            value={formData.email}
            required
          />
          <input
            name="password"
            type="password"
            placeholder="Password"
            onChange={handleChange}
            value={formData.password}
            required
          />
          {error && <p style={{ color: 'red' }}>{error}</p>}
          <button type="submit">Register</button>
          <button type="button" className="close-btn" onClick={closeForm}>
            Close
          </button>
        </form>
      </div>
    </div>
  );
};

export default Register;
