// src/pages/Dashboard.js
import React, { useEffect, useState } from 'react';
import API from '../api';
import PostForm from '../components/PostForm';

const Dashboard = () => {
  const [posts, setPosts] = useState([]);
  const [showPostForm, setShowPostForm] = useState(false);

  // Fetch posts from backend
  const fetchPosts = async () => {
    try {
      const token = localStorage.getItem('token');
      const { data } = await API.get('/posts', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setPosts(data);
    } catch (error) {
      console.error('Error fetching posts:', error);
    }
  };

  useEffect(() => {
    fetchPosts(); // Initial fetch of posts
  }, []);

  const handlePostCreated = () => {
    fetchPosts(); // Refresh posts after a new post is created
    setShowPostForm(false); // Hide the form after submission
  };

  const togglePostForm = () => {
    setShowPostForm(!showPostForm); // Toggle the "Create Post" form
  };

  return (
    <div>
      <h1>Your Dashboard</h1>

      {/* Button to toggle the Create Post form */}
      <button onClick={togglePostForm}>
        {showPostForm ? 'Close Post Form' : 'Create New Post'}
      </button>

      {/* Conditionally render the Create Post form */}
      {showPostForm && <PostForm onPostCreated={handlePostCreated} />}

      {/* Display Recent Posts with Downloadable Files */}
      <div>
        <h2>Recent Posts</h2>
        {posts.length > 0 ? (
          posts.map((post) => (
            <div key={post._id}>
              <h3>{post.title}</h3>
              <p>Language: {post.language}</p>

              {/* Download link for the code snippet file */}
              {post.codeFileUrl && (
                <p>
                  Code Snippet File:{' '}
                  <a href={post.codeFileUrl} download>
                    Download Code
                  </a>
                </p>
              )}

              {/* Download link for the uploaded file */}
              {post.uploadedFileUrl && (
                <p>
                  Uploaded File:{' '}
                  <a href={post.uploadedFileUrl} download>
                    Download File
                  </a>
                </p>
              )}
            </div>
          ))
        ) : (
          <p>No posts available</p>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
