// src/pages/Dashboard.js
import React, { useEffect, useState } from 'react';
import API from '../api';
import PostForm from '../components/PostForm';
import io from 'socket.io-client';
import '../styles/Dashboard.css';

const socket = io('http://localhost:5000'); // Connect to the server

const Dashboard = () => {
  const [posts, setPosts] = useState([]);
  const [showPostForm, setShowPostForm] = useState(false);
  const [notifications, setNotifications] = useState([]);
  const [showNotificationDot, setShowNotificationDot] = useState(false);

  const username = localStorage.getItem('username');
  const token = localStorage.getItem('token');

  // Fetch posts and unseen notifications from the backend
  const fetchPostsAndNotifications = async () => {
    try {
      // Fetch recent posts
      const { data: postData } = await API.get('/posts', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setPosts(postData);

      // Fetch unseen notifications for the logged-in user
      const { data: unseenNotifications } = await API.get('/notifications/unseen', {
        headers: { Authorization: `Bearer ${token}` },
      });
      if (unseenNotifications.length > 0) {
        setNotifications(unseenNotifications);
        setShowNotificationDot(true); // Show the red dot if there are unseen notifications
      }
    } catch (error) {
      console.error('Error fetching posts or notifications:', error);
    }
  };

  useEffect(() => {
    fetchPostsAndNotifications();

    // Real-time listener for new post notifications
    socket.on('newPostNotification', (notification) => {
      if (notification.authorUsername !== username) {
        setNotifications((prev) => [notification, ...prev]);
        setShowNotificationDot(true); // Show the red dot on the notification button
      }
    });

    return () => {
      socket.off('newPostNotification'); // Clean up the socket listener on component unmount
    };
  }, [username]);

  // Refresh posts when a new post is created
  const handlePostCreated = () => {
    fetchPostsAndNotifications();
    setShowPostForm(false);
  };

  const togglePostForm = () => {
    setShowPostForm(!showPostForm);
  };

  // Handle notification button click to clear the red dot and show notifications
  const handleNotificationClick = () => {
    setShowNotificationDot(false);
    setNotifications([]); // Clear notifications when viewed
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('username');
    window.location.href = '/';
  };

  return (
    <div className="dashboard">
      <div className="dashboard-header">
        <div className="username-box">
          <span>{username}</span>
        </div>

        <button onClick={togglePostForm}>Create Post</button>

        <div className="notification-box" onClick={handleNotificationClick}>
          <button>Notification</button>
          {showNotificationDot && <span className="notification-dot"></span>}
        </div>
      </div>

      {/* Post form component */}
      {showPostForm && <PostForm onPostCreated={handlePostCreated} />}

      {/* Display posts in the dashboard */}
      <div className="posts-container">
        <h2>Recent Posts</h2>
        {posts.length > 0 ? (
          posts.map((post) => (
            <div key={post._id} className="post-item">
              <p className="author-name">Posted by: {post.authorUsername || 'Anonymous'}</p>
              <h3>{post.title}</h3>
              {/* Conditionally render language if it exists */}
              {post.language && <p>Language: {post.language}</p>}
              {post.codeFileUrl && (
                <p>
                  Code Snippet File: <a href={post.codeFileUrl} download>Download Code</a>
                </p>
              )}
              {post.uploadedFileUrl && (
                <p>
                  Uploaded File: <a href={post.uploadedFileUrl} download>Download File</a>
                </p>
              )}
            </div>
          ))
        ) : (
          <p>No posts available</p>
        )}
      </div>

      {/* Logout button at the bottom */}
      <button className="logout-button" onClick={handleLogout}>Logout</button>
    </div>
  );
};

export default Dashboard;
