import '../styles/PostForm.css';

const PostForm = ({ onPostCreated }) => {
  const [formData, setFormData] = useState({
    title: '', // Ensure title is correctly initialized
    codeSnippet: '',
    language: '',
  });
  const [file, setFile] = useState(null);
  const [error, setError] = useState('');

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
    setError('');
  };

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Check if title is provided before submitting
    if (!formData.title.trim()) {
      setError("Title is required.");
      console.error("Title field is empty.");
      return;
    }

    const token = localStorage.getItem('token');
    const formDataObj = new FormData();
    formDataObj.append('title', formData.title.trim()); // Required field
    if (formData.codeSnippet) formDataObj.append('codeSnippet', formData.codeSnippet);
    if (formData.language) formDataObj.append('language', formData.language);
    if (file) formDataObj.append('file', file);

    console.log("Submitting Form Data:", { title: formData.title, language: formData.language, file });

    try {
      await API.post('/posts/create', formDataObj, {
        headers: {
          Authorization: `Bearer ${token}`,
          'Content-Type': 'multipart/form-data',
        },
      });
      alert('Post created successfully');
      onPostCreated();
    } catch (error) {
      console.error('Error creating post:', error); // Log the full error for inspection
      setError('Failed to create post. Please check if all required fields are filled and try again.');
    }
  };

  return (
    <form onSubmit={handleSubmit} className="post-form">
      <h2>Create Post</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <label>Post Title</label>
      <input
        name="title"
        placeholder="Post Title"
        value={formData.title}
        onChange={handleChange}
        required
      />

      <label>Code Snippet</label>
      <textarea
        name="codeSnippet"
        placeholder="Code Snippet (optional)"
        value={formData.codeSnippet}
        onChange={handleChange}
      />

      <label>Extension</label>
      <select name="language" value={formData.language} onChange={handleChange}>
        <option value="">Select Language</option>
        <option value="javascript">JavaScript</option>
        <option value="python">Python</option>
        <option value="cpp">C++</option>
        <option value="java">Java</option>
      </select>

      <label>File</label>
      <input type="file" onChange={handleFileChange} />

      <button type="submit">Post</button>
    </form>
  );
};

export default PostForm;
