// src/pages/Dashboard.js
import React, { useEffect, useState } from 'react';
import API from '../api';
import PostForm from '../components/PostForm';
import io from 'socket.io-client';
import '../styles/Dashboard.css'; // Assuming you’ll put the CSS in this file

const socket = io('http://localhost:5000'); // Connect to the server

const Dashboard = () => {
  const [posts, setPosts] = useState([]);
  const [showPostForm, setShowPostForm] = useState(false);
  const [notifications, setNotifications] = useState([]);
  const [showNotificationDot, setShowNotificationDot] = useState(false);

  // Get the current username from localStorage
  const username = localStorage.getItem('username');

  const fetchPosts = async () => {
    try {
      const token = localStorage.getItem('token');
      const { data } = await API.get('/posts', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setPosts(data);
    } catch (error) {
      console.error('Error fetching posts:', error);
    }
  };

  useEffect(() => {
    fetchPosts();
    
    // Listen for new post notifications
    socket.on('newPostNotification', (notification) => {
      if (notification.author !== username) {
        setNotifications((prevNotifications) => [notification, ...prevNotifications]);
        setShowNotificationDot(true);
      }
    });

    return () => {
      socket.off('newPostNotification'); // Clean up socket listener
    };
  }, [username]);

  const handlePostCreated = () => {
    fetchPosts();
    setShowPostForm(false);
  };

  const togglePostForm = () => {
    setShowPostForm(!showPostForm);
  };

  const handleNotificationClick = () => {
    setShowNotificationDot(false);
    setNotifications([]); // Clear notifications after viewing
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('username');
    window.location.href = '/'; // Redirect to homepage
  };

  return (
    <div className="dashboard">
      <div className="dashboard-header">
        <div className="username-box">{username}</div> {/* Username in a purple box */}
        <button onClick={togglePostForm}>Create Post</button>
        <div className="notification-box" onClick={handleNotificationClick}>
          <button>Notification</button>
          {showNotificationDot && <span className="notification-dot"></span>}
          {notifications.length > 0 && (
            <div className="notification-popup">
              <h4>New Posts</h4>
              {notifications.map((notification, index) => (
                <div key={index}>
                  <p>{notification.title}</p>
                  <span>{new Date(notification.createdAt).toLocaleString()}</span>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>

      {showPostForm && <PostForm onPostCreated={handlePostCreated} />}

      <div className="posts-container">
        <h2>Recent Posts</h2>
        {posts.length > 0 ? (
          posts.map((post) => (
            <div key={post._id} className="post-item">
              <h3>{post.title}</h3>
              <p>Language: {post.language}</p>
              {post.codeFileUrl && (
                <p>
                  Code Snippet File: <a href={post.codeFileUrl} download>Download Code</a>
                </p>
              )}
              {post.uploadedFileUrl && (
                <p>
                  Uploaded File: <a href={post.uploadedFileUrl} download>Download File</a>
                </p>
              )}
            </div>
          ))
        ) : (
          <p>No posts available</p>
        )}
      </div>

      <button className="logout-button" onClick={handleLogout}>Logout</button>
    </div>
  );
};

export default Dashboard;
