// src/components/UnreadPostsModal.js
import React from 'react';
import API from '../api';

const UnreadPostsModal = ({ notifications, onPostOpen }) => {
  const handlePostClick = async (postId) => {
    try {
      // Fetch post details using only the postId
      const { data } = await API.get(`/posts/${postId}`);
      onPostOpen(data); // Handle opening the post in a new tab or modal
    } catch (error) {
      console.error("Error fetching post:", error);
    }
  };

  return (
    <div className="unread-posts-modal">
      <h3>Unread Posts</h3>
      {notifications.map((notification) => (
        <div key={notification._id} className="notification-item">
          <p>
            Posted by: <a href="#" onClick={() => handlePostClick(notification.postId)}>{notification.authorUsername}</a>
          </p>
        </div>
      ))}
    </div>
  );
};

export default UnreadPostsModal;
