// src/pages/Dashboard.js
import React, { useEffect, useState } from 'react';
import API from '../api';
import PostForm from '../components/PostForm';
import Notification from '../components/Notification';
import PostList from '../components/PostList';

const Dashboard = () => {
  const [posts, setPosts] = useState([]);
  const [showPostForm, setShowPostForm] = useState(false);
  const currentUserId = localStorage.getItem('userId');

  const fetchPosts = async () => {
    try {
      const token = localStorage.getItem('token');
      const { data } = await API.get('/posts', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setPosts(data);
    } catch (error) {
      console.error('Error fetching posts:', error);
    }
  };

  useEffect(() => {
    fetchPosts();
  }, []);

  const handlePostCreated = () => {
    fetchPosts();
    setShowPostForm(false);
  };

  const handleNewPostNotification = (newPost) => {
    // Append the new post to the list when notification is clicked
    setPosts((prevPosts) => [newPost, ...prevPosts]);
  };

  return (
    <div>
      <h1>Your Dashboard</h1>

      {/* Notification Button */}
      <Notification currentUserId={currentUserId} onNewPost={handleNewPostNotification} />

      <button onClick={() => setShowPostForm(!showPostForm)}>
        {showPostForm ? 'Close Post Form' : 'Create New Post'}
      </button>
      {showPostForm && <PostForm onPostCreated={handlePostCreated} />}

      <PostList posts={posts} />
    </div>
  );
};

export default Dashboard;
