// src/components/UnreadPostsModal.js
import React from 'react';
import API from '../api';

const UnreadPostsModal = ({ notifications, onClose, onPostClick }) => {
  const handlePostClick = async (postId) => {
    try {
      const { data } = await API.get(`/posts/${postId}`);
      window.open(`/posts/${postId}`, '_blank');
      onPostClick(postId); // Mark the notification as read
    } catch (error) {
      console.error("Error fetching post:", error);
    }
  };

  return (
    <div className="unread-posts-modal">
      <button onClick={onClose} className="close-modal-button">Close</button>
      <h3>Unread Posts</h3>
      {notifications.map((notification) => (
        <div key={notification._id} className="notification-item">
          <p>
            Posted by: <a href="#" onClick={() => handlePostClick(notification.postId)}>{notification.authorUsername || 'Anonymous'}</a>
          </p>
        </div>
      ))}
    </div>
  );
};

export default UnreadPostsModal;
